ST.xhtml:
PLC_RUN:= (PLC_R.i_wStatus = 16#0002);
PLS_RESET(CLK:= PLC_RUN);

Cycle.CW.xAuxON:= PLC_RUN;
Cycle.SW.xON:= (Cycle.CW.xStart OR Cycle.SW.xON) AND NOT Cycle.CW.xAuxStop;  

CycleDelay(IN:= Cycle.SW.xON,PT:= T#3S);
PulseStart(CLK:= CycleDelay.Q AND (Cycle.CW.xStart OR NOT Cycle.SW.xRunning), Q=> Cycle.CW.xPulseStart);

Cycle.SW.xRunning:= CycleDelay.Q;
				
Cycle.CW.xStart:= FALSE;
IF Cycle.CW.xAuxStop THEN
	Cycle.CW.xStop:= FALSE;
	Cycle.CW.xAuxStop:= FALSE;
END_IF

//=====================================================================================================================//
// Definition of pumps ready to start
//=====================================================================================================================//
xPMP1_StartRdy:= PMP1.SW.xAutoOK AND NOT PMP1.SW.xRUN AND
				((PMP1_WorkTimer[3] <= PMP2_WorkTimer[3]) OR NOT PMP2.SW.xAutoOK OR PMP2.SW.xRUN) AND
				((PMP1_WorkTimer[3] <= PMP3_WorkTimer[3]) OR NOT PMP3.SW.xAutoOK OR PMP3.SW.xRUN);
xPMP2_StartRdy:= PMP2.SW.xAutoOK AND NOT PMP2.SW.xRUN AND
				((PMP2_WorkTimer[3] <= PMP1_WorkTimer[3]) OR NOT PMP1.SW.xAutoOK OR PMP1.SW.xRUN) AND 
				((PMP2_WorkTimer[3] <= PMP3_WorkTimer[3]) OR NOT PMP3.SW.xAutoOK OR PMP3.SW.xRUN);
xPMP3_StartRdy:= PMP3.SW.xAutoOK AND NOT PMP3.SW.xRUN AND 
				((PMP3_WorkTimer[3] <= PMP1_WorkTimer[3]) OR NOT PMP1.SW.xAutoOK OR PMP1.SW.xRUN) AND 
				((PMP3_WorkTimer[3] <= PMP2_WorkTimer[3]) OR NOT PMP2.SW.xAutoOK OR PMP2.SW.xRUN);

//=====================================================================================================================//
// Identifying a pump ready to stop
//=====================================================================================================================//
xPMP1_StopRdy:= PMP1.SW.xAutoOK AND PMP1.SW.xRUN AND 
				((PMP1_WorkTimer[3] >= PMP2_WorkTimer[3]) OR NOT PMP2.SW.xRUN) AND 
				((PMP1_WorkTimer[3] >= PMP3_WorkTimer[3]) OR NOT PMP3.SW.xRUN);
xPMP2_StopRdy:= PMP2.SW.xAutoOK AND PMP2.SW.xRUN AND 
				((PMP2_WorkTimer[3] >= PMP1_WorkTimer[3]) OR NOT PMP1.SW.xRUN) AND 
				((PMP2_WorkTimer[3] >= PMP3_WorkTimer[3]) OR NOT PMP3.SW.xRUN);
xPMP3_StopRdy:= PMP3.SW.xAutoOK AND PMP3.SW.xRUN AND 
				((PMP3_WorkTimer[3] >= PMP1_WorkTimer[3]) OR NOT PMP1.SW.xRUN) AND 
				((PMP3_WorkTimer[3] >= PMP2_WorkTimer[3]) OR NOT PMP2.SW.xRUN);
				
//=====================================================================================================================//
// Determining the number of operating pumps and the minimum output value of the main PID controller
//=====================================================================================================================//
IF (PMP1.SW.xRUN AND PMP1.SW.xAutoOK) AND (PMP2.SW.xRUN AND PMP2.SW.xAutoOK) AND(PMP3.SW.xRUN AND PMP3.SW.xAutoOK) THEN
	MainReg_Ymin:= MAX(MAX(PMP1.rMinFreq, PMP2.rMinFreq), PMP3.rMinFreq);
	iRunPmpNum:= 3;
ELSIF ((PMP1.SW.xRUN AND PMP1.SW.xAutoOK) AND (PMP2.SW.xRUN AND PMP2.SW.xAutoOK)) THEN
	MainReg_Ymin:= MAX(PMP1.rMinFreq, PMP2.rMinFreq);
	iRunPmpNum:= 2;
ELSIF ((PMP1.SW.xRUN AND PMP1.SW.xAutoOK) AND (PMP3.SW.xRUN AND PMP3.SW.xAutoOK)) THEN
	MainReg_Ymin:= MAX(PMP1.rMinFreq, PMP3.rMinFreq);
	iRunPmpNum:= 2; 
ELSIF ((PMP2.SW.xRUN AND PMP2.SW.xAutoOK) AND (PMP3.SW.xRUN AND PMP3.SW.xAutoOK)) THEN
	MainReg_Ymin:= MAX(PMP2.rMinFreq, PMP3.rMinFreq);
	iRunPmpNum:= 2;
ELSIF (PMP1.SW.xRUN AND PMP1.SW.xAutoOK) THEN
	MainReg_Ymin:= PMP1.rMinFreq;
	iRunPmpNum:= 1;
ELSIF (PMP2.SW.xRUN AND PMP2.SW.xAutoOK) THEN
	MainReg_Ymin:= PMP2.rMinFreq;
	iRunPmpNum:= 1;
ELSIF (PMP3.SW.xRUN AND PMP3.SW.xAutoOK) THEN
	MainReg_Ymin:= PMP3.rMinFreq;
	iRunPmpNum:= 1;
ELSE
	MainReg_Ymin:= 25.0;
	iRunPmpNum:= 0;
END_IF
			
//====================================================================================================================//
// Pump start request condition
//====================================================================================================================//
AuxPmpOnTmr(IN:= ((PMP1.rActFreq >= (PMP1.rMaxFreq - 1.0)) OR
				  (PMP2.rActFreq >= (PMP2.rMaxFreq - 1.0)) OR
				  (PMP3.rActFreq >= (PMP3.rMaxFreq - 1.0))) AND (xPMP1_StartRdy OR xPMP2_StartRdy OR xPMP3_StartRdy) AND
				 ((BP01.RealValue + 0.3) <= Cycle.rSP) AND Cycle.SW.xRunning, PT:= T#180S, ET=> AuxPmpOnTmrET);
AuxPmpOnPls(CLK:= AuxPmpOnTmr.Q);
//====================================================================================================================//
// Pump shutdown request condition
//====================================================================================================================//
AuxPmpOffTmr(IN:= (((PMP1.rActFreq < Cycle.PmpDisconnFreq) AND xPMP1_StopRdy) OR
				  ((PMP2.rActFreq < Cycle.PmpDisconnFreq) AND xPMP2_StopRdy) OR
				  ((PMP3.rActFreq < Cycle.PmpDisconnFreq) AND xPMP3_StopRdy)) AND (iRunPmpNum > 1) AND
				  ((BP01.RealValue + 0.2) >= Cycle.rSP) AND Cycle.SW.xRunning, PT:= T#180S, ET=> AuxPmpOffTmrET);
AuxPmpOffPls(CLK:= AuxPmpOffTmr.Q);
//====================================================================================================================//
// Time rotation of pumps
//====================================================================================================================//
IF Cycle.CW.xPmpRot AND (xPMP1_StartRdy OR xPMP2_StartRdy OR xPMP3_StartRdy) AND (PLC_RTC.wHour = Cycle.uiRotHour) AND (PLC_RTC.wMinute = Cycle.uiRotMin) AND (PLC_RTC.wSecond = 1) THEN
	RotReq:= TRUE;
END_IF

IF DisconnPls.Q OR NOT Cycle.CW.xPmpRot OR ((PMP1_Rising AND PMP1.SW.xAlarmAccum) OR (PMP2_Rising AND PMP2.SW.xAlarmAccum) OR (PMP3_Rising AND PMP3.SW.xAlarmAccum) AND NOT (xPMP1_StartRdy OR xPMP2_StartRdy OR xPMP3_StartRdy)) THEN
	RotReq:= FALSE;
END_IF

ConnPls(CLK:= RotReq);
DisconnPls(CLK:= RotReq AND (AuxPID.Y >= MainPID.Y));
//====================================================================================================================//
// Generation of pump start-up and shutdown requests
//====================================================================================================================//
StartPulse(CLK:= Cycle.SW.xRunning);
IF Cycle.SW.xRunning THEN
	IF StartPulse.Q OR AuxPmpOnPls.Q OR (iRunPmpNum = 0) OR ConnPls.Q OR (PMP1_Rising AND PMP1.SW.xAlarmAccum) OR (PMP2_Rising AND PMP2.SW.xAlarmAccum) OR (PMP3_Rising AND PMP3.SW.xAlarmAccum) THEN
		xStartAuxPmpReq:= TRUE;
	END_IF

	IF (AuxPmpOffPls.Q OR DisconnPls.Q) AND (iRunPmpNum > 1) THEN
		xStopAuxPmpReq:= TRUE;
	END_IF

ELSE	
	xStartAuxPmpReq:= FALSE;
	xStopAuxPmpReq:= FALSE;
	PMP1.CW.xAutoON:= FALSE;
	PMP2.CW.xAutoON:= FALSE;
	PMP3.CW.xAutoON:= FALSE;
	PMP1_Rising:= FALSE;
	PMP2_Rising:= FALSE;
	PMP3_Rising:= FALSE;
	RotReq:= FALSE;
END_IF

//====================================================================================================================//
// Formation of an acceleration bit on request to start a pump unit or connect an additional pump
//====================================================================================================================//
IF xStartAuxPmpReq THEN
	IF xPMP1_StartRdy THEN
		PMP1_Rising:= TRUE;
	ELSIF xPMP2_StartRdy THEN
		PMP2_Rising:= TRUE;
	ELSIF xPMP3_StartRdy THEN
		PMP3_Rising:= TRUE;
	END_IF
	xStartAuxPmpReq:= FALSE;
END_IF
//====================================================================================================================//
// Setpoint reduction on acceleration
//====================================================================================================================//
IF PMP1_Rising OR PMP2_Rising OR PMP3_Rising THEN
	MainPID.SetPoint:= Cycle.rSP - 1.0;			
	AuxPID.SetPoint:= Cycle.rSP;				
ELSE
	MainPID.SetPoint:= Cycle.rSP;
	AuxPID.SetPoint:= 0.0;
END_IF
//====================================================================================================================//
// Pump No. 1 run-up
//====================================================================================================================// 	
IF PMP1_Rising THEN														
	PMP1.CW.xAutoON:= TRUE;													
	PMP1.rAutoFreq:= AuxPID_SetFreq;										
	                                                                              
	IF AuxPID.Y >= MainPID.Y OR PMP1.SW.xAlarmAccum THEN					
		PMP1_Rising:= FALSE;												
	END_IF                                                                        
ELSIF NOT (xStopAuxPmpReq AND xPMP1_StopRdy) AND NOT Cycle.CW.xStop THEN	 
	PMP1.rAutoFreq:= MainPID_SetFreq;										
END_IF
//====================================================================================================================//
// Pump No. 2 run-up
//====================================================================================================================//
IF PMP2_Rising THEN
	PMP2.CW.xAutoON:= TRUE;
	PMP2.rAutoFreq:= AuxPID_SetFreq;		
	
	IF AuxPID.Y >= MainPID.Y OR PMP2.SW.xAlarmAccum THEN
		PMP2_Rising:= FALSE;
	END_IF
ELSIF NOT (xStopAuxPmpReq AND xPMP2_StopRdy) AND NOT Cycle.CW.xStop THEN
	PMP2.rAutoFreq:= MainPID_SetFreq;
END_IF
//====================================================================================================================//
// Pump No. 3 run-up
//====================================================================================================================//
IF PMP3_Rising THEN
	PMP3.CW.xAutoON:= TRUE;
	PMP3.rAutoFreq:= AuxPID_SetFreq;		

	IF AuxPID.Y >= MainPID.Y OR PMP3.SW.xAlarmAccum THEN
		PMP3_Rising:= FALSE;
	END_IF
ELSIF NOT (xStopAuxPmpReq AND xPMP3_StopRdy) AND NOT Cycle.CW.xStop THEN
	PMP3.rAutoFreq:= MainPID_SetFreq;
END_IF

//====================================================================================================================//
// Smooth stop of redundant pump
//====================================================================================================================//
CyclicSubTimer(IN:= NOT CyclicSubTimer.Q, PT:= T#5S);
CyclicSubPls(CLK:= CyclicSubTimer.Q);

IF xStopAuxPmpReq  THEN
	IF xPMP1_StopRdy THEN
		IF CyclicSubPls.Q THEN
			PMP1.rAutoFreq:= PMP1.rAutoFreq - 2.0;
		END_IF
		IF PMP1.rActFreq <= PMP1.rMinFreq + 2.0 THEN
			PMP1.CW.xAutoON:= FALSE;
			xStopAuxPmpReq:= FALSE;
		END_IF
	ELSIF xPMP2_StopRdy THEN
		IF CyclicSubPls.Q THEN
			PMP2.rAutoFreq:= PMP2.rAutoFreq - 2.0;
		END_IF
		IF PMP2.rActFreq <= PMP2.rMinFreq + 2.0 THEN
			PMP2.CW.xAutoON:= FALSE;
			xStopAuxPmpReq:= FALSE;
		END_IF
	ELSIF xPMP3_StopRdy THEN
		IF CyclicSubPls.Q THEN
			PMP3.rAutoFreq:= PMP3.rAutoFreq - 2.0;
		END_IF
		IF PMP3.rActFreq <= PMP3.rMinFreq + 2.0 THEN
			PMP3.CW.xAutoON:= FALSE;
			xStopAuxPmpReq:= FALSE;
		END_IF
	END_IF
END_IF

//====================================================================================================================//
// Resetting the start command in automatic cycle if the pump is not in automatic mode
//====================================================================================================================//
IF NOT PMP1.SW.xAutoOK THEN
	PMP1.CW.xAutoON:= FALSE;
END_IF	
IF NOT PMP2.SW.xAutoOK THEN
	PMP2.CW.xAutoON:= FALSE;
END_IF	
IF NOT PMP3.SW.xAutoOK THEN
	PMP3.CW.xAutoON:= FALSE;
END_IF	

//====================================================================================================================//
// Soft stop of pumps to disable automatic cycle
//====================================================================================================================//
IF Cycle.CW.xStop AND Cycle.SW.xRunning THEN
	xStartAuxPmpReq:= FALSE;
	xStopAuxPmpReq:= FALSE;
	IF CyclicSubPls.Q THEN
		PMP1.rAutoFreq:= PMP1.rAutoFreq - 2.0;
		PMP2.rAutoFreq:= PMP2.rAutoFreq - 2.0;
		PMP3.rAutoFreq:= PMP3.rAutoFreq - 2.0;
	END_IF
	
	IF ((PMP1.rActFreq <= PMP1.rMinFreq + 2.0) OR NOT PMP1.SW.xAutoOK) AND 
		((PMP2.rActFreq <= PMP2.rMinFreq + 2.0) OR NOT PMP2.SW.xAutoOK) AND 
		((PMP3.rActFreq <= PMP3.rMinFreq + 2.0) OR NOT PMP3.SW.xAutoOK) THEN
		Cycle.CW.xAuxStop:= TRUE;
	END_IF
END_IF

//====================================================================================================================//
// Determining the minimum output value of the auxiliary PID controller
//====================================================================================================================//
IF PMP1_Rising THEN
	AuxReg_Ymin:= PMP1.rMinFreq;
ELSIF PMP2_Rising THEN
	AuxReg_Ymin:= PMP2.rMinFreq;
ELSIF PMP3_Rising THEN
	AuxReg_Ymin:= PMP3.rMinFreq;
ELSE
	AuxReg_Ymin:= 25.0;
END_IF

//====================================================================================================================//
// Main PID controller
//====================================================================================================================//
MainPID.CW.Reset:= NOT(Cycle.SW.xRunning AND (PMP1.SW.xRunAuto OR PMP2.SW.xRunAuto OR PMP3.SW.xRunAuto));
MainPID.ActValue:= BP01.RealValue;
MainReg(
	ACTUAL:= MainPID.ActValue, 
	SET_POINT:= MainPID.SetPoint, 
	KP:= MainPID.Kp, 
	TN:= MainPID.Tn, 
	TV:= MainPID.Tv, 
	Y_MANUAL:= MainPID.ManVal, 
	Y_OFFSET:= MainPID.Offset, 
	Y_MIN:= MainReg_Ymin * 10.0,
	Y_MAX:= 500.0, 
	MANUAL:= MainPID.CW.ManMode, 
	RESET:= MainPID.CW.Reset, 
	Y=> MainPID.Y, 
	LIMITS_ACTIVE=> , 
	OVERFLOW=> );
MainPID_SetFreq:= MainPID.Y / 10.0; 

//====================================================================================================================//
// Auxiliary PID controller used to accelerate the auxiliary pump
//====================================================================================================================//	
AuxPID.CW.Reset:= NOT(Cycle.SW.xRunning AND (PMP1_Rising OR PMP2_Rising OR PMP3_Rising));	
AuxPID.ActValue:= BP01.RealValue;
AuxReg(
	ACTUAL:= AuxPID.ActValue, 
	SET_POINT:= AuxPID.SetPoint, 
	KP:= AuxPID.Kp, 
	TN:= AuxPID.Tn, 
	TV:= AuxPID.Tv, 
	Y_MANUAL:= AuxPID.ManVal, 
	Y_OFFSET:= AuxPID.Offset, 
	Y_MIN:= AuxReg_Ymin * 10.0,
	Y_MAX:= 500.0, 
	MANUAL:= AuxPID.CW.ManMode, 
	RESET:= AuxPID.CW.Reset, 
	Y=> AuxPID.Y, 
	LIMITS_ACTIVE=> , 
	OVERFLOW=> );
AuxPID_SetFreq:= AuxPID.Y / 10.0;

	(*Alarm Feedback processing*)
TON_Overtime(IN:=Mot_strct.CW.xEnable AND ((Mot_strct.SW.xCOM AND NOT Mot_strct.SW.xRUN) OR (NOT Mot_strct.SW.xCOM AND Mot_strct.SW.xRUN)),PT:=SetOvertime);

IF TON_Overtime.Q THEN
	xAlarmFeedback:=TRUE;
END_IF; 

IF NOT Mot_strct.CW.xExtraEn AND xCOM THEN
	xAlarmBlock:= TRUE;
END_IF

Mot_strct.SW.xAlarmAccum:= xAlarmFeedback OR Mot_strct.CW.xExtraAlarm OR xAlarmBlock;

IF Mot_strct.CW.xACK THEN
	xAlarmFeedback:= FALSE;
	xAlarmBlock:= FALSE;
END_IF
	
	(*Auto/Manual mode selection*)
IF Mot_strct.CW.xAutoMode THEN
	xEnableMotorAuto:= TRUE;
ELSIF Mot_strct.CW.xManMode THEN
	xEnableMotorAuto:= FALSE;
END_IF;
Mot_strct.SW.xManAuto:= xEnableMotorAuto; 

Mot_strct.SW.xManOK:= NOT Mot_strct.SW.xAlarmAccum AND Mot_strct.CW.xAuxON AND Mot_strct.CW.xEnable AND NOT xEnableMotorAuto;
Mot_strct.SW.xAutoOK:= NOT Mot_strct.SW.xAlarmAccum AND Mot_strct.CW.xAuxON AND Mot_strct.CW.xEnable AND xEnableMotorAuto;

	(*Motor ready to start*)
Mot_strct.SW.xReady:= (Mot_strct.SW.xManOK OR Mot_strct.SW.xAutoOK) AND NOT Mot_strct.SW.xAlarmAccum;

	(*Manual mode activation*)
IF Mot_strct.CW.xManStart THEN
	Mot_strct.SW.xRunManual:=TRUE;
END_IF;
IF Mot_strct.CW.xManStop OR NOT Mot_strct.SW.xManOK THEN
	Mot_strct.SW.xRunManual:=FALSE;
END_IF;

	(*Auto mode activation*)
Mot_strct.SW.xRunAuto:= Mot_strct.SW.xAutoOK AND Cycle.SW.xRunning;

	(*Set command for start*)
xCOM:=((Mot_strct.SW.xRunManual AND Mot_strct.SW.xManOK) OR (Mot_strct.SW.xRunAuto AND Mot_strct.SW.xAutoOK AND Mot_strct.CW.xAutoON));
Mot_strct.SW.xCOM:= xCOM AND NOT Mot_strct.CW.xSimEn;

	(*Work time counter*)
WTC(StartCount:= Mot_strct.SW.xRUN, udiSeconds=> WorkTime[4], udiMinutes=> WorkTime[3], udiHours=> WorkTime[2], udiDays=> WorkTime[1]);

	(*Frequency limitation*)
Mot_strct.rMaxFreq:= LIMIT(0.0, Mot_strct.rMaxFreq, 50.0);
Mot_strct.rMinFreq:= LIMIT(0.0, Mot_strct.rMinFreq, Mot_strct.rMaxFreq);	
Mot_strct.rManFreq:= LIMIT(Mot_strct.rMinFreq, Mot_strct.rManFreq, Mot_strct.rMaxFreq);
Mot_strct.rAutoFreq:= LIMIT(Mot_strct.rMinFreq, Mot_strct.rAutoFreq, Mot_strct.rMaxFreq);

IF Mot_strct.SW.xRunAuto THEN
	rFreqValue:= Mot_strct.rAutoFreq;
ELSIF Mot_strct.SW.xRunManual THEN
	rFreqValue:= Mot_strct.rManFreq;
ELSE
	rFreqValue:= 0.0;
END_IF;

	(*Status processing*)
IF Mot_strct.SW.xAlarmAccum THEN											//Alarm
	Mot_strct.enStatus:= MTR_Stat.ALARM_ACCUM;
ELSIF Mot_strct.SW.xCOM AND Mot_strct.SW.xRunManual THEN					//Work in manual mode
	Mot_strct.enStatus:= MTR_Stat.RUN_MAN;
ELSIF Mot_strct.SW.xManOK THEN												//Stand in manual mode
	Mot_strct.enStatus:= MTR_Stat.STAND_MAN;	
ELSIF Mot_strct.SW.xCOM AND Mot_strct.SW.xRunAuto THEN						//Work in auto mode
	Mot_strct.enStatus:= MTR_Stat.RUN_AUTO;
ELSIF Mot_strct.SW.xRunAuto THEN											//Stand in auto mode
	Mot_strct.enStatus:= MTR_Stat.STAND_AUTO;
ELSIF Mot_strct.SW.xAutoOK THEN												//In auto mode, but not in auto cycle
	Mot_strct.enStatus:= MTR_Stat.NOT_IN_CYCLE;			
ELSIF NOT Mot_strct.CW.xExtraEn THEN										//Block
	Mot_strct.enStatus:= MTR_Stat.BLOCK ;					
ELSE																		//Standby
	Mot_strct.enStatus:= MTR_Stat.STANDBY;								
END_IF;

Mot_strct.CW.xAutoMode:= FALSE;
Mot_strct.CW.xManMode:= FALSE;
Mot_strct.CW.xManStart:= FALSE;
Mot_strct.CW.xManStop:= FALSE;
Mot_strct.CW.xACK:= FALSE;

	//Work time counter
PLS(CLK:= PLC_R.i_x1sClk);
IF StartCount AND PLS.Q THEN
	udiCount:= udiCount + 1;
END_IF

udiSeconds:= udiCount;
udiMinutes:= udiSeconds / 60;
udiHours:= udiSeconds / 3600;
udiDays:= udiSeconds / 86400;

Cycle.SW.xALARM:= PMP1.SW.xAlarmAccum OR 
				PMP2.SW.xAlarmAccum OR 
				PMP3.SW.xAlarmAccum OR
				NOT Cycle.WRN.xVoltageOK;

(*TON_Restart(IN:= Cycle.SW.xRunning AND Cycle.WRN.xVoltageOK, PT:= T#3S);				
PLS_Restart(CLK:= TON_Restart.Q);*)	
				
IF Cycle.CW.xACK OR ixACK OR PLS_Restart.Q THEN
	PMP1.CW.xACK:= TRUE;
	PMP2.CW.xACK:= TRUE;
	PMP3.CW.xACK:= TRUE;
	Cycle.WRN.xPMP1_Dry:= FALSE;
	Cycle.WRN.xPMP2_Dry:= FALSE;
	Cycle.WRN.xPMP3_Dry:= FALSE;
	Cycle.CW.xACK:= FALSE;
END_IF

A00_IW( W:= iwDI_IW0, B00=> VoltageOK_1, B01=> , B02=> , B03=> , B04=> , B05=> , B06=> , B07=> , B08=> , B09=> , B10=> , B11=> , B12=> , B13=> , B14=> , B15=> );
A00_QW( B00:= , B01:= , B02:= , B03:= , B04:= Cycle.SW.xALARM, B05:= , B06:= , B07:= , B08:= , B09:= , B10:= , B11:= , B12:= , B13:= , B14:= , B15:= , W=> qwDQ_QW0);
A01( W:= iwA01_IW0, B00=> VoltageOK_2, B01=> ixACK, B02=> PMP1.SW.xRem, B03=> PMP1.SW.xRUN, B04=> PMP2.SW.xRem, B05=> PMP2.SW.xRUN, B06=> PMP3.SW.xRem, B07=> PMP3.SW.xRUN, B08=> Cycle.WRN.xDrainLL, B09=> Cycle.WRN.xDrainHL, B10=> Cycle.WRN.xDrainHH, B11=> , B12=> , B13=> , B14=> Cycle.WRN.xFire, B15=> Cycle.WRN.xFireEqipFault);
A02( B00:= Cycle.SW.xALARM, //Alarm Lamp B01:= Cycle.WRN.xPLC_ConectOK, B02:= PMP1.SW.xCOM, B03:= PMP2.SW.xCOM, B04:= PMP3.SW.xCOM, B05:= , B06:= , B07:= , B08:= , B09:= , B10:= , B11:= , B12:= , B13:= , B14:= , B15:= , W=> qwA02_QB0);
Cycle.WRN.xVoltageOK:= VoltageOK_1 AND VoltageOK_2; Cycle.WRN.xPLC_ConectOK:= plc_r.i_lwSystemFault_1.1;

PMP1.CW.xAuxON:= Cycle.CW.xAuxON;
PMP1.CW.xEnable:= PMP1.SW.xRem;
PMP1.CW.xExtraAlarm:= Cycle.WRN.xPMP1_Dry OR (PMP1.dwFC_AlarmWord <> 0);
PMP1.CW.xExtraEn:= TRUE;
PMP1_FB(
	SetOvertime:= T#10S, 
	Cycle:= Cycle, 
	rFreqValue=> , 
	WorkTime=> PMP1_WorkTimer, 
	Mot_strct:= PMP1);
PMP1.rActFreq:= WORD_TO_REAL(wFreq_VFD_1) / 10.0;
PMP1.rCurrent:= WORD_TO_REAL(wI_VFD_1) / 100.0;
PMP1.rPower:= WORD_TO_REAL(wPower_VFD_1) / 1000.0;
PMP1.rRunHours:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= CountWork_VFD_1_1, wLowWord:= CountWork_VFD_1_2));
PMP1.rkWhCount:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= wEnergyCount_VFD_1_1, wLowWord:= wEnergyCount_VFD_1_2));
PMP1.wFC_StatusWord:= wStatus_VFD_1;
PMP1.dwFC_AlarmWord:= MEM.PackWordsToDword(wHighWord:= wFault_VFD_1_1, wLowWord:= wFault_VFD_1_2);
PMP1.rHeatsinkTemp:= WORD_TO_REAL(wT_freq_1);

	
IF PMP1.SW.xManAuto THEN
	rVFD1_SetFreq:= PMP1.rAutoFreq;
ELSE
	rVFD1_SetFreq:= PMP1.rManFreq;
END_IF;	

VFD1_REF_SCALLING(IN:= rVFD1_SetFreq, IN_MIN:= 0.0, IN_MAX:= 50.0, OUT_MIN:= 16#0, OUT_MAX:= 16#4000);	
iFreq_Set_1:= REAL_TO_WORD(VFD1_REF_SCALLING.OUT);

IF BP12.SW.LL_alarm AND PMP1.SW.xRUN THEN
	Cycle.WRN.xPMP1_Dry:= TRUE;
END_IF;

	
	

PMP2.CW.xAuxON:= Cycle.CW.xAuxON;	
PMP2.CW.xEnable:= PMP2.SW.xRem;
PMP2.CW.xExtraAlarm:= Cycle.WRN.xPMP2_Dry OR (PMP2.dwFC_AlarmWord <> 0);
PMP2.CW.xExtraEn:= TRUE;
PMP2_FB(
	SetOvertime:= T#10S, 
	Cycle:= Cycle, 
	rFreqValue=> , 
	WorkTime=> PMP2_WorkTimer, 
	Mot_strct:= PMP2);
PMP2.rActFreq:= WORD_TO_REAL(wFreq_VFD_2) / 10.0;
PMP2.rCurrent:= WORD_TO_REAL(wI_VFD_2) / 100.0;
PMP2.rPower:= WORD_TO_REAL(wPower_VFD_2) / 1000.0;
PMP2.rRunHours:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= CountWork_VFD_2_1, wLowWord:= CountWork_VFD_2_2));
PMP2.rkWhCount:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= wEnergyCount_VFD_2_1, wLowWord:= wEnergyCount_VFD_2_2));
PMP2.wFC_StatusWord:= wStatus_VFD_2;
PMP2.dwFC_AlarmWord:= MEM.PackWordsToDword(wHighWord:= wFault_VFD_2_1, wLowWord:= wFault_VFD_2_2);
PMP2.rHeatsinkTemp:= WORD_TO_REAL(wT_freq_2);

IF PMP2.SW.xManAuto THEN
	rVFD2_SetFreq:= PMP2.rAutoFreq;
ELSE
	rVFD2_SetFreq:= PMP2.rManFreq;
END_IF;	
VFD2_REF_SCALLING(IN:= rVFD2_SetFreq, IN_MIN:= 0.0, IN_MAX:= 50.0, OUT_MIN:= 16#0, OUT_MAX:= 16#4000);	
iFreq_Set_2:= REAL_TO_WORD(VFD2_REF_SCALLING.OUT);	
	
IF BP22.SW.LL_alarm AND PMP2.SW.xRUN THEN
	Cycle.WRN.xPMP2_Dry:= TRUE;
END_IF;




PMP3.CW.xAuxON:= Cycle.CW.xAuxON;
PMP3.CW.xEnable:= PMP3.SW.xRem;
PMP3.CW.xExtraAlarm:= Cycle.WRN.xPMP3_Dry OR (PMP3.dwFC_AlarmWord <> 0);	
PMP3.CW.xExtraEn:= TRUE;	
PMP3_FB(
	SetOvertime:= T#10S, 
	Cycle:= Cycle, 
	rFreqValue=> , 
	WorkTime=> PMP3_WorkTimer, 
	Mot_strct:= PMP3);
PMP3.rActFreq:= WORD_TO_REAL(wFreq_VFD_3) / 10.0;
PMP3.rCurrent:= WORD_TO_REAL(wI_VFD_3) / 100.0;
PMP3.rPower:= WORD_TO_REAL(wPower_VFD_3) / 1000.0;
PMP3.rRunHours:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= CountWork_VFD_3_1, wLowWord:= CountWork_VFD_3_2));
PMP3.rkWhCount:= DWORD_TO_REAL(MEM.PackWordsToDword(wHighWord:= wEnergyCount_VFD_3_1, wLowWord:= wEnergyCount_VFD_3_2));
PMP3.wFC_StatusWord:= wStatus_VFD_3;
PMP3.dwFC_AlarmWord:= MEM.PackWordsToDword(wHighWord:= wFault_VFD_3_1, wLowWord:= wFault_VFD_3_2);
PMP3.rHeatsinkTemp:= WORD_TO_REAL(wT_freq_3);

	
IF PMP3.SW.xManAuto THEN
	rVFD3_SetFreq:= PMP3.rAutoFreq;
ELSE
	rVFD3_SetFreq:= PMP3.rManFreq;
END_IF;	
VFD3_REF_SCALLING(IN:= rVFD3_SetFreq, IN_MIN:= 0.0, IN_MAX:= 50.0, OUT_MIN:= 16#0, OUT_MAX:= 16#4000);	
iFreq_Set_3:= REAL_TO_WORD(VFD3_REF_SCALLING.OUT);

IF BP32.SW.LL_alarm AND PMP3.SW.xRUN THEN
	Cycle.WRN.xPMP3_Dry:= TRUE;
END_IF;



(*
PMP_DRAIN.CW.xAuxON:= Cycle.CW.xAuxON;
PMP_DRAIN.CW.xEnable:= PMP_DRAIN.SW.xRem;
PMP_DRAIN.CW.xExtraAlarm:= FALSE;
PMP_DRAIN_FB(
	SetOvertime:= T#5S, 
	Cycle:= Cycle, 
	rFreqValue=> , 
	WorkTime=> PMP_DRAIN_WorkTimer, 
	Mot_strct:= PMP_DRAIN);

*)

//Output collector pressure
BP00.PIW:= iiA1_1_IW2;
BP00.CW.ExtFault:= (ibA1_1_IBStatusIW2 <> 0);
BP00.k:= 16.0;
BP00.ADC_Range:= 10000;
BP00.CW.bar_kgf:= TRUE;
BP00_FB(Snsr_strct:= BP00);

//Input collector pressure
BP01.PIW:= iiA1_1_IW0;
BP01.CW.ExtFault:= (ibA1_1_IBStatusIW0 <> 0);
BP01.k:= 16.0;
BP01.ADC_Range:= 10000;
BP01.CW.bar_kgf:= TRUE;
BP01_FB(Snsr_strct:= BP01);


//Pump 1 inlet pressure
BP11.PIW:= iiA03_IW0;
BP11.CW.ExtFault:= (ibA03_IBStatusIW0 <> 0);
BP11.k:= 6.0;
BP11.b:= -1.0;
BP11.ADC_Range:= 10000;
BP11.CW.bar_kgf:= TRUE;
BP11.CW.LimEn:= FALSE;
BP11.LL:= 1.0;
BP11.DeadBand:= 0.5;
BP11.LimDelay:= 5;
BP11_FB(Snsr_strct:= BP11);

//Pump 1 outlet pressure
BP12.PIW:= iiA03_IW1;
BP12.CW.ExtFault:= (ibA03_IBStatusIW1 <> 0);
BP12.k:= 10.0;
BP12.ADC_Range:= 10000;
BP12.CW.bar_kgf:= TRUE;
BP12.CW.LimEn:= PMP1.SW.xRUN;
BP12.LL:= BP11.RealValue + 1.0;
BP12.DeadBand:= 0.0;
BP12.LimDelay:= 60;
BP12_FB(Snsr_strct:= BP12);

//Pump 2 inlet pressure
BP21.PIW:= iiA03_IW2;
BP21.CW.ExtFault:= (ibA03_IBStatusIW2 <> 0);
BP21.k:= 6.0;
BP21.b:= -1.0;
BP21.ADC_Range:= 10000;
BP21.CW.bar_kgf:= TRUE;
BP21.CW.LimEn:= FALSE;
BP21.LL:= 1.0;
BP21.DeadBand:= 0.5;
BP21.LimDelay:= 5;
BP21_FB(Snsr_strct:= BP21);

//Pump 2 outlet pressure
BP22.PIW:= iiA03_IW3;
BP22.CW.ExtFault:= (ibA03_IBStatusIW3 <> 0);
BP22.k:= 10.0;
BP22.ADC_Range:= 10000;
BP22.CW.bar_kgf:= TRUE;
BP22.CW.LimEn:= PMP2.SW.xRUN;
BP22.LL:= BP21.RealValue + 1.0;
BP22.DeadBand:= 0.0; 
BP22.LimDelay:= 60;
BP22_FB(Snsr_strct:= BP22);

//Pump 3 inlet pressure
BP31.PIW:= iiA03_IW4;
BP31.CW.ExtFault:= (ibA03_IBStatusIW4 <> 0);
BP31.k:= 6.0;
BP31.b:= -1.0;
BP31.ADC_Range:= 10000;
BP31.CW.bar_kgf:= TRUE;
BP31.CW.LimEn:= FALSE;
BP31.LL:= 1.0;
BP31.DeadBand:= 0.5;
BP31.LimDelay:= 10;
BP31_FB(Snsr_strct:= BP31);

//Pump 3 outlet pressure
BP32.PIW:= iiA03_IW5;
BP32.CW.ExtFault:= (ibA03_IBStatusIW5 <> 0);
BP32.k:= 10.0;
BP32.ADC_Range:= 10000;
BP32.CW.bar_kgf:= TRUE;
BP32.CW.LimEn:= PMP3.SW.xRUN;
BP32.LL:= BP31.RealValue + 1.0;
BP32.DeadBand:= 0.0;
BP32.LimDelay:= 60;
BP32_FB(Snsr_strct:= BP32);


//Tank level
BL00.PIW:= iiA1_1_IW1;
BL00.CW.ExtFault:= (ibA1_1_IBStatusIW1 <> 0);
BL00.k:= 5.0;
BL00.ADC_Range:= 10000;
BL00.CW.bar_kgf:= FALSE;
BL00_FB(Snsr_strct:= BL00);

//Internal temperature
BT00.PIW:= iiA1_1_IW3;
BT00.CW.ExtFault:= (ibA1_1_IBStatusIW3 <> 0);
BT00.k:= 1000.0;
BT00.ADC_Range:= 10000;
BT00.CW.bar_kgf:= FALSE;
BT00_FB(Snsr_strct:= BT00);

// Fault operation
IF Snsr_strct.PIW < 0 OR Snsr_strct.PIW > Snsr_strct.ADC_Range OR Snsr_strct.CW.ExtFault THEN		(*Snsr_strct Failed*)
	Snsr_strct.SW.Fault:= TRUE;
	RealValue:= 0.0;
ELSE                                                                    			(*Snsr_strct OK*)
	IF Snsr_strct.ADC_Range <= 0 THEN
		Snsr_strct.ADC_Range:= 1;
	END_IF
	Snsr_strct.SW.Fault:= FALSE;
	RealValue:= Snsr_strct.k * bar_kgf * INT_TO_REAL(Snsr_strct.PIW) / INT_TO_REAL(Snsr_strct.ADC_Range) + Snsr_strct.b;	
END_IF

// Converting from bar to kgf/sm2
IF Snsr_strct.CW.bar_kgf THEN
	bar_kgf:= 1.0197;
ELSE
	bar_kgf:= 1.0;
END_IF		


// SMA filter operation
CycTmr(IN:= NOT CycTmr.Q AND INIT, PT:= INT_TO_TIME(Snsr_strct.SamplingTime / 1000));	(*Sample puls generator*)
IF Snsr_strct.CW.FltrEn THEN
	IF NOT INIT THEN																	(*Initialization*)
		FOR y:= 0 TO 9 DO
			Buff[y]:= RealValue;
			SumValue:= SumValue + Buff[y];
		END_FOR
		INIT:= TRUE;
	END_IF
	
	IF CycTmr.Q THEN																	(*Buffer shifting and changing last element*)
		FOR y:= 0 TO 8 DO
			Buff[y]:= Buff[y + 1];
		END_FOR
		Buff[9]:= RealValue;
		SumValue:= SumValue + RealValue - Buff[0];											
	END_IF
	FltrValue:= SumValue / 10;
ELSE
	INIT:= FALSE;
	SumValue:= 0.0;
	FltrValue:= 0.0;
END_IF	

// Snsr_strct output values states
IF Snsr_strct.CW.SimEn THEN															(*Simulated output value*)						
	Snsr_strct.RealValue:= Snsr_strct.SimValue;
	Snsr_strct.IntValue:= REAL_TO_INT(Snsr_strct.SimValue * 100.0);
ELSIF Snsr_strct.CW.FltrEn THEN														(*Filtered output value*)
	Snsr_strct.RealValue:= FltrValue;
	Snsr_strct.IntValue:= REAL_TO_INT(FltrValue * 100.0);
ELSE																				(*Real output value*)
	Snsr_strct.RealValue:= RealValue;
	Snsr_strct.IntValue:= REAL_TO_INT(RealValue * 100.0);
END_IF

// Limits operation
tonHHTopDelay(IN:= (Snsr_strct.RealValue >= Snsr_strct.HH + Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));
tonHHBotDelay(IN:= (Snsr_strct.RealValue <= Snsr_strct.HH - Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));	
tonHLTopDelay(IN:= (Snsr_strct.RealValue >= Snsr_strct.HL + Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));
tonHLBotDelay(IN:= (Snsr_strct.RealValue <= Snsr_strct.HL - Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));

tonLHTopDelay(IN:= (Snsr_strct.RealValue <= Snsr_strct.LH - Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));
tonLHBotDelay(IN:= (Snsr_strct.RealValue >= Snsr_strct.LH + Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));
tonLLTopDelay(IN:= (Snsr_strct.RealValue <= Snsr_strct.LL - Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));
tonLLBotDelay(IN:= (Snsr_strct.RealValue >= Snsr_strct.LL + Snsr_strct.DeadBand / 2.0) AND Snsr_strct.CW.LimEn, PT:= INT_TO_TIME(Snsr_strct.LimDelay * 1000));

IF tonHHTopDelay.Q THEN
	Snsr_strct.SW.HH_alarm:= TRUE;
END_IF	
IF tonHHBotDelay.Q THEN
	Snsr_strct.SW.HH_alarm:= FALSE;
END_IF
IF tonHLTopDelay.Q THEN
	Snsr_strct.SW.HL_alarm:= TRUE;
END_IF
IF tonHLBotDelay.Q THEN
	Snsr_strct.SW.HL_alarm:= FALSE;
END_IF
IF tonLHTopDelay.Q THEN
	Snsr_strct.SW.LH_alarm:= TRUE;
END_IF
IF tonLHBotDelay.Q THEN
	Snsr_strct.SW.LH_alarm:= FALSE;
END_IF
IF tonLLTopDelay.Q THEN
	Snsr_strct.SW.LL_alarm:= TRUE;
END_IF
IF tonLLBotDelay.Q THEN
	Snsr_strct.SW.LL_alarm:= FALSE;
END_IF
	
IF NOT Snsr_strct.CW.LimEn THEN
	Snsr_strct.SW.HH_alarm:= FALSE;
	Snsr_strct.SW.HL_alarm:= FALSE;
	Snsr_strct.SW.LH_alarm:= FALSE;
	Snsr_strct.SW.LL_alarm:= FALSE;	
END_IF

	//Alarm processing
FeedBackAlrmTmr(IN:= Vlv_strct.CW.xEnable AND (COM_Close OR COM_Open) AND NOT Vlv_strct.SW.xRUN, PT:= T#3S);
AlrmNotMoveTmr(IN:= Vlv_strct.CW.xEnable AND ((COM_Close AND NOT Vlv_strct.SW.xClosed) OR (COM_Open AND NOT Vlv_strct.SW.xOpened), PT:= SetOvertime);

IF FeedBackAlrmTmr.Q THEN 
	AlrmFeedBack:= TRUE;
END_IF

IF AlrmNotMoveTmr.Q THEN 
	AlrmNotMove:= TRUE;
END_IF

IF Vlv_strct.SW.xOpened AND Vlv_strct.SW.xClosed THEN
	AlrmSQ:= TRUE;
END_IF

IF NOT Vlv_strct.CW.xExtraEn AND (COM_Open OR COM_Close) THEN
	AlarmBlock:= TRUE;
END_IF

Vlv_strct.SW.xAlarmAccum:= AlrmFeedBack OR AlrmNotMove OR AlrmSQ OR AlarmBlock OR Vlv_strct.CW.xExtraAlarm; 

IF Vlv_strct.CW.xACK THEN
	AlrmSQ:= FALSE;
	AlrmNotMove:= FALSE;
	AlrmFeedBack:= FALSE;
	AlarmBlock:= FALSE;
END_IF

	//Auto/Manual mode selection
IF Vlv_strct.CW.xAutoMode THEN
	EnableVlvAuto:= TRUE;
END_IF
IF Vlv_strct.CW.xManMode THEN
	EnableVlvAuto:= FALSE;
END_IF
Vlv_strct.SW.xManAuto:= EnableVlvAuto;
	
Vlv_strct.SW.xManOK:=NOT Vlv_strct.SW.xAlarmAccum AND Vlv_strct.CW.xAuxON AND Vlv_strct.CW.xEnable AND NOT EnableVlvAuto;
Vlv_strct.SW.xAutoOK:= NOT Vlv_strct.SW.xAlarmAccum AND Vlv_strct.CW.xAuxON AND Vlv_strct.CW.xEnable AND EnableVlvAuto AND Cycle.SW.xRunning;
	
	//Run in manual mode
IF Vlv_strct.CW.xManOpen OR Vlv_strct.CW.xManClose THEN
	Vlv_strct.SW.xRunManual:= TRUE;
END_IF
	
IF Vlv_strct.CW.xStop OR NOT IF Vlv_strct.CW.xStop OR NOT Vlv_strct.SW.xManOK THEN
	Vlv_strct.SW.xRunManual:= FALSE;
END_IF
	
	//Run in auto mode
Vlv_strct.SW.xRunAuto:= Cycle.SW.xRunning AND Vlv_strct.SW.xAutoOK;
	
	//Open/Close commands
COM_Open:= ((Vlv_strct.CW.xAutoOpen AND Vlv_strct.SW.xRunAuto AND Vlv_strct.SW.xAutoOK) OR ((Vlv_strct.CW.xManOpen OR COM_Open) AND Vlv_strct.SW.xRunManual AND Vlv_strct.SW.xManOK)) AND NOT COM_Close AND NOT Vlv_strct.SW.xOpened;
COM_Close:= ((Vlv_strct.CW.xAutoClose AND Vlv_strct.SW.xRunAuto AND Vlv_strct.SW.xAutoOK) OR ((Vlv_strct.CW.xManClose OR COM_Close) AND Vlv_strct.SW.xRunManual AND Vlv_strct.SW.xManOK)) AND NOT COM_Open AND NOT Vlv_strct.SW.xClosed;

IF Vlv_strct.CW.xSimEn THEN
	Vlv_strct.SW.xCOM_Open:= FALSE;
	Vlv_strct.SW.xCOM_Close:= FALSE;	
ELSE
	Vlv_strct.SW.xCOM_Open:= COM_Open;
	Vlv_strct.SW.xCOM_Close:= COM_Close;	
END_IF
	
	//Status processing
IF Vlv_strct.SW.xAlarmAccum  THEN
	Vlv_strct.wStatus:= VLV_Stat.ALARM_ACCUM;
ELSIF Vlv_strct.SW.xOpened THEN
	Vlv_strct.wStatus:= VLV_Stat.OPENED;
ELSIF Vlv_strct.SW.xClosed THEN
	Vlv_strct.wStatus:= VLV_Stat.CLOSED;
ELSIF NOT Vlv_strct.CW.xExtraEn THEN
	Vlv_strct.wStatus:= VLV_Stat.BLOCK;
ELSE
	Vlv_strct.wStatus:= VLV_Stat.STANDBY;
END_IF

	//Reset control bits
Vlv_strct.CW.xAutoMode:= FALSE;
Vlv_strct.CW.xManMode:= FALSE;
Vlv_strct.CW.xManOpen:= FALSE;
Vlv_strct.CW.xStop:= FALSE;
Vlv_strct.CW.xManClose:= FALSE;
Vlv_strct.CW.xACK:= FALSE;

IF pNtpServer_Addr='' THEN
	pNtpServer_Addr := '172.16.34.34';	

END_IF

socketHandle := SysSockCreate(SOCKET_AF_INET, SOCKET_DGRAM, SOCKET_IPPROTO_UDP, ADR(tcpError)); // SOCKET_DGRAM

IF TcpError =0 THEN 
        
   sendbuf[1]:=16#DB; //    NTP Client V3

// memset();
   socketAddress.sin_zero[0] := 0;
   socketAddress.sin_zero[1] := 0;
   socketAddress.sin_zero[2] := 0;
   socketAddress.sin_zero[3] := 0;
   socketAddress.sin_zero[4] := 0;
   socketAddress.sin_zero[5] := 0;
   socketAddress.sin_zero[6] := 0;
   socketAddress.sin_zero[7] := 0;
   socketAddress.sin_family:= SOCKET_AF_INET;
   socketAddress.sin_port:= SysSockHtons(123); // setting the port of the socket 37 - NTP

// Add to IP
   SysSockInetAddr(pNtpServer_Addr, ADR(socketAddress.sin_addr.ulAddr)); // setting the address of the socket     
   tcpError:= SysSockConnect(socketHandle, ADR(socketAddress), SIZEOF(socketAddress));
   IF TcpError =0 THEN 
      sentBytes:= SysSockSend(hSocket:= socketHandle, pbyBuffer:= ADR(sendbuf), diBufferSize:=SIZEOF(sendbuf), diFlags:= 0, pResult:= ADR(tcpError));
   END_IF
   IF TcpError =0 AND sentBytes = SIZEOF(sendbuf) THEN 
    REPEAT
		 receivedBytes:= SysSockGetRecvSizeUdp(hSocketUdp:= socketHandle, diTimeout :=0, pResult := ADR(tcpError));
		 IF receivedBytes=48 AND tcpError=0 THEN
           receivedBytes:= SysSockRecv(hSocket:= socketHandle, pbyBuffer:= ADR(receiveBuffer), diBufferSize:= SIZEOF(receiveBuffer), diFlags:= 0, pResult:= ADR(tcpError));		 
		 ELSE
  	        wRepeat := wRepeat+1;    
            sysTaskWaitSleep(0,1000);
         END_IF
         UNTIL (receivedBytes<>0 OR tcpError<>0) OR (wRepeat>=10)  // so wait 10 sec for very slow connection
      END_REPEAT;
   END_IF
   SysSockClose(socketHandle);
END_IF

pResult := tcpError;

IF receivedBytes <>48 OR pResult <> 0 THEN
    TimeNTPGet := 0;    //
	IF pResult = 0 THEN     // if we have no ethernet communication issue then use the timeout error code
		pResult := SEN.TimedOut;   // No valid response from NTP server
	END_IF
ELSE
	dwDateTime:=receiveBuffer[33];
    dwDateTime:=SHL(dwDateTime,8) + receiveBuffer[34];
    dwDateTime:=SHL(dwDateTime,8) + receiveBuffer[35];
    dwDateTime:=SHL(dwDateTime,8) + receiveBuffer[36];
    TimeNTPGet:= dwDateTime - secFrom1900to1970 + wRepeat;  // time correction for long response time
END_IF

IF xAuto THEN
		fb_cycle(IN:=NOT fb_cycle.Q, PT:=tSync);
END_IF
IF fb_cycle.Q THEN
	xPing:=1;
END_IF
IF xPing THEN
dwLocalDateTime := TimeNTPGet(pNtpServer_Addr := IP_addr, pResult := eError);
IF eError = 0 THEN                   // if no error
	IF xSync THEN
		sysTimeRTCSet (dwLocalDateTime);
	END_IF
	SysTimeRtcConvertUtcToDate (dwTimestampUtc:=dwLocalDateTime,pDate:=ServerDate);
   xNTPSynchronously := TRUE;
ELSE
   xNTPSynchronously := FALSE; 
END_IF
END_IF

xPing := FALSE;   // event Get Time from NTP Server now completed 

RtcTime:=SysTimeRtcGet(pResult:=l_udiResult)+iBias*3600; 
SysTimeRtcConvertUtcToDate (dwTimestampUtc:=RtcTime,pDate:=PLC_time);

