wPrsMonthS:=LIMIT(1,wPrsMonthS,12);
wPrsMonthW:=LIMIT(1,wPrsMonthW,12);
wPrsDayS:=LIMIT(1, WORD_TO_UINT(wPrsDayS), QDay(y:=wYear , m:=WORD_TO_UINT(wPrsMonthS)));
wPrsDayW:=LIMIT(1, WORD_TO_UINT(wPrsDayW), QDay(y:=wYear , m:=WORD_TO_UINT(wPrsMonthW)));

IF wPrsMonthW > wPrsMonthS OR wPrsMonthW = wPrsMonthS AND wPrsDayW > wPrsDayS THEN
	IF wMonth>wPrsMonthW OR wMonth=wPrsMonthW AND wDay>=wPrsDayW OR wMonth<wPrsMonthS OR wMonth=wPrsMonthS AND wDay<wPrsDayS THEN
		Summer:=0;
		wCmdSP.10:=0;
	ELSIF wMonth>wPrsMonthS OR wMonth=wPrsMonthS AND wDay>=wPrsDayS THEN
		Summer:=1;
		wCmdSP.10:=1;
	END_IF
