IF wPrsHourSP_2>wPrsHourSP_1 OR wPrsHourSP_2=wPrsHourSP_1 AND wPrsMinSP_2>wPrsMinSP_1 THEN
	IF wHour>wPrsHourSP_2 OR wHour=wPrsHourSP_2 AND wMinute>=wPrsMinSP_2 OR wHour<wPrsHourSP_1 OR wHour=wPrsHourSP_1 AND wMinute<wPrsMinSP_1 THEN
		IF NOT write_sp.1 THEN
			write_sp:=0;
			SP_in:=rPrsSP_2;
			write_sp.1:=1;
		END_IF
	ELSIF wHour>wPrsHourSP_1 OR wHour=wPrsHourSP_1 AND wMinute>=wPrsMinSP_1 THEN
		IF NOT write_sp.0 THEN
			write_sp:=0;
			SP_in:=rPrsSP_1;
			write_sp.0:=1;
		END_IF
	END_IF
ELSE 
	IF wHour>wPrsHourSP_1 OR wHour=wPrsHourSP_1 AND wMinute>=wPrsMinSP_1 OR wHour<wPrsHourSP_2 OR wHour=wPrsHourSP_2 AND wMinute<wPrsMinSP_2 THEN
			IF NOT write_sp.2 THEN
			write_sp:=0;
			SP_in:=rPrsSP_1;
			write_sp.2:=1;
		END_IF
	ELSIF wHour>wPrsHourSP_2 OR wHour=wPrsHourSP_2 AND wMinute>=wPrsMinSP_2 THEN
