xPrsSP_N:=wCmdSP.0;
	IF xPrsSP_N THEN
		CASE wNumSP OF
			1: rPrsSP_1:=rPrsDN;
			wPrsHourSP_1:=wPrsHourSP_N;
			wPrsMinSP_1:=wPrsMinSP_N;
			2: rPrsSP_2:=rPrsDN;
			wPrsHourSP_2:=wPrsHourSP_N;
			wPrsMinSP_2:=wPrsMinSP_N;						
			3: rPrsSP_3:=rPrsDN;
			wPrsHourSP_3:=wPrsHourSP_N;
			wPrsMinSP_3:=wPrsMinSP_N;			
			4: rPrsSP_4:=rPrsDN;
			wPrsHourSP_4:=wPrsHourSP_N;
			wPrsMinSP_4:=wPrsMinSP_N;
		END_CASE
		xPrsSP_N:=0;
		wCmdSP.0:=0;
	END_IF
xPrsSchedSP:=wCmdSP.1;
	IF xPrsSchedSP THEN
		SP_in2:=rPrsSched;
		xPrsSchedSP:=0;
		wCmdSP.1:=0;
	END_IF
CASE rem_auto_man OF 
	1: SP_target:=rSP_manual;
	2: SP_target:=SP_in;
	3: IF xSCADA_Comm THEN
		SP_target:=SP_in2;	
		ELSE
		SP_target:=SP_in;
		END_IF;
END_CASE;
IF rSP_manual <> SP_old_man THEN
	CASE rem_auto_man OF 
		2: SP_in:=rSP_manual;
		3: SP_in2:=rSP_manual;
			SP_in:=rSP_manual;
	END_CASE;
	SP_old_man:=rSP_manual;
END_IF;
speed_limit(SP_target:= SP_target, dSP:= r_dSP, PLC_Cycle:= PLC_Cycle, Translate:= 1);
SP:=speed_limit.SP;

rCurA:=dataset.stCurrent.rLine1;
rCurB:=dataset.stCurrent.rLine2;
rCurC:=dataset.stCurrent.rLine3;
rCurAvg:=dataset.stCurrent.rAverage;
rVol12:=dataset.stVoltage.rLine12;
rVol23:=dataset.stVoltage.rLine23;
rVol31:=dataset.stVoltage.rLine31;
rVolAvg:=dataset.stVoltage.rLineLavg;
// Мощность активная
rTotPwrA:=dataset.stPower.rReTotal/1000;
// Мощность реактивная
rTotPwrR:=dataset.stPower.rRvTotal/1000;
// apparent power  полная мощность
rTotPwrAp:=dataset.stPower.rApTotal/1000;
rPwrFact:=dataset.stPowerFactor.rTotal;
rCurFrq:=dataset.stFrequency.rFrequency;
rEnergyA:=dataset.stEnergy.rReal;
rEnergyR:=dataset.stEnergy.rRctv;


lowwrd1:=modbus_slave_ch6^[0];
highwrd1:=modbus_slave_ch6^[1];
rEnergyA_H:=MEM.PackWordsToDword(highwrd1,lowwrd1);
lowwrd2:=modbus_slave_ch6^[2];
highwrd2:=modbus_slave_ch6^[3];
rEnergyA_L:=MEM.PackWordsToDword(highwrd2,lowwrd2);
lowwrd3:=modbus_slave_ch6^[4];
highwrd3:=modbus_slave_ch6^[5];
rEnergyR_H:=MEM.PackWordsToDword(highwrd3,lowwrd3);
lowwrd4:=modbus_slave_ch6^[6];
highwrd4:=modbus_slave_ch6^[7];
rEnergyR_L:=MEM.PackWordsToDword(highwrd4,lowwrd4);

//dpos:=TIME_TO_INT
dpos:=(100/(TIME_TO_REAL(time_move)/1000))*PLC_Cycle;
IF open AND pos < 100 THEN
	IF ABS(100-pos) >= dpos THEN
		pos:=pos+dpos;
	ELSE
		pos:=100;
	END_IF;
END_IF;
IF close AND pos > 0 THEN
	IF ABS(pos) >= dpos THEN
		pos:=pos-dpos;
	ELSE
		pos:=0;
	END_IF;
END_IF;

q_Out:=k*INT_TO_REAL(i_sensor_value)/INT_TO_REAL(i_ADC_Range)+b;
q_status_analog:=i_status_analog;

IF q_status_analog=0 THEN
xAlarm_bit:=0;
ELSE
	IF q_status_analog>0 THEN
		xAlarm_bit:=1;	
	END_IF;
END_IF;

// Date input restriction
SetPoint.sSPDate.wSummMonth:= LIMIT(1, SetPoint.sSPDate.wSummMonth, 12);
SetPoint.sSPDate.wWinMonth:= LIMIT(1, SetPoint.sSPDate.wWinMonth, 12);
SetPoint.sSPDate.wSummDay:= LIMIT(1, SetPoint.sSPDate.wSummDay, 31);
SetPoint.sSPDate.wWinDay:= LIMIT(1, SetPoint.sSPDate.wWinDay, 31);

// Convert the date of type “mm:dd” to a number
wSummDate:= (SetPoint.sSPDate.wSummMonth * 100) + SetPoint.sSPDate.wSummDay;
wWinDate:= (SetPoint.sSPDate.wWinMonth * 100) + SetPoint.sSPDate.wWinDay;
wCurrDate:= (PLC_RTC.wMonth * 100) + PLC_RTC.wDay;

// Defining the summer period
xSummer:= ((wCurrDate >= wSummDate) AND (wCurrDate < wWinDate)) OR ((wSummDate > wWinDate) AND ((wCurrDate >= wSummDate) OR (wCurrDate < wWinDate)));
// Defining the winter period
xWinter:= (wCurrDate >= wWinDate) AND (wCurrDate < wSummDate) OR ((wWinDate > wSummDate) AND ((wCurrDate >= wWinDate) OR (wCurrDate < wSummDate)));




// Recording time and setpoints in the schedule
IF SetPoint.CW.xSPLocSet THEN
	SetPoint.arrSPLocCurr[SetPoint.wSPLocSetNum].wHour:= SetPoint.sSPLocSet.wHour;
	SetPoint.arrSPLocCurr[SetPoint.wSPLocSetNum].wMinute:= SetPoint.sSPLocSet.wMinute;
	SetPoint.arrSPLocCurr[SetPoint.wSPLocSetNum].rSP:= SetPoint.sSPLocSet.rSP;
	SetPoint.CW.xSPLocSet:= FALSE;
END_IF

// Time entry restriction 
SetPoint.sSPLocSet.wHour:= LIMIT(0, SetPoint.sSPLocSet.wHour, 24);
SetPoint.sSPLocSet.wMinute:= LIMIT(0, SetPoint.sSPLocSet.wMinute, 59);
FOR i:= 1 TO 4 DO
	SetPoint.arrSPLocCurr[i].wHour:= LIMIT(0, SetPoint.arrSPLocCurr[i].wHour, 24);
	SetPoint.arrSPLocCurr[i].wMinute:= LIMIT(0, SetPoint.arrSPLocCurr[i].wMinute, 59);
END_FOR

// Convert time type “hh:mm” to a number
wSummDayTime:= (SetPoint.arrSPLocCurr[1].wHour * 100) + SetPoint.arrSPLocCurr[1].wMinute; 
wSummNightTime:= (SetPoint.arrSPLocCurr[2].wHour * 100) + SetPoint.arrSPLocCurr[2].wMinute; 
wWinDayTime:= (SetPoint.arrSPLocCurr[3].wHour * 100) + SetPoint.arrSPLocCurr[3].wMinute; 
wWinNightTime:= (SetPoint.arrSPLocCurr[4].wHour * 100) + SetPoint.arrSPLocCurr[4].wMinute;
wCurrTime:= (PLC_RTC.wHour * 100) + PLC_RTC.wMinute; 

// Determine the day and night time of the summer period and set the corresponding setpoint number
IF xSummer THEN
	IF ((wCurrTime >= wSummDayTime) AND (wCurrTime < wSummNightTime)) OR ((wSummDayTime > wSummNightTime) AND ((wCurrTime >= wSummDayTime) OR (wCurrTime < wSummNightTime))) THEN
		SetPoint.wSPLocCurrNum:= 1;
	ELSIF ((wCurrTime >= wSummNightTime) AND (wCurrTime < wSummDayTime)) OR ((wSummNightTime > wSummDayTime) AND ((wCurrTime >= wSummNightTime) OR (wCurrTime < wSummDayTime))) THEN
		SetPoint.wSPLocCurrNum:= 2;
	END_IF
END_IF

// Determine the day and night time of the winter period and set the corresponding setpoint number
IF xWinter THEN
	IF ((wCurrTime >= wWinDayTime) AND (wCurrTime < wWinNightTime)) OR ((wWinDayTime > wWinNightTime) AND ((wCurrTime >= wWinDayTime) OR (wCurrTime < wWinNightTime))) THEN
		SetPoint.wSPLocCurrNum:= 3;
	ELSIF ((wCurrTime >= wWinNightTime) AND (wCurrTime < wWinDayTime)) OR ((wWinNightTime > wWinDayTime) AND ((wCurrTime >= wWinNightTime) OR (wCurrTime < wWinDayTime))) THEN
		SetPoint.wSPLocCurrNum:= 4;
	END_IF
END_IF




IF SetPoint.CW.xManMode THEN															// Selecting the manual setpoint mode											
	SetPoint.SW.xManual:= TRUE;
	SetPoint.SW.xSched:= FALSE;
	SetPoint.SW.xRemote:= FALSE;
	SetPoint.CW.xManMode:= FALSE;
ELSIF SetPoint.CW.xAutoLocMode THEN														// Selecting the automatic setpoint mode											
	SetPoint.SW.xManual:= FALSE;
	SetPoint.SW.xSched:= TRUE;
	SetPoint.SW.xRemote:= FALSE;
	SetPoint.CW.xAutoLocMode:= FALSE;
ELSIF SetPoint.CW.xAutoRemMode THEN														// Selecting the automatic remote setpoint mode											
	SetPoint.SW.xManual:= FALSE;
	SetPoint.SW.xSched:= FALSE;
	SetPoint.SW.xRemote:= TRUE;
	SetPoint.CW.xAutoRemMode:= FALSE;
ELSIF NOT SetPoint.SW.xManual AND NOT SetPoint.SW.xSched AND NOT SetPoint.SW.xRemote THEN
	SetPoint.SW.xSched:= TRUE;
END_IF 

// SCADA communication check
ConectCheck(IN:= SetPoint.CW.xConnect OR SetPoint.wConnect.2, PT:= T#3M); 	

xSchedPLS(CLK:= SetPoint.SW.xSched);																				// Pulse to switch to schedule mode
xSchedSP1_PLS(CLK:= SetPoint.SW.xSched AND (SetPoint.wSPLocCurrNum = 1));											// Pulse of the 1st setpoint on schedule
xSchedSP2_PLS(CLK:= SetPoint.SW.xSched AND (SetPoint.wSPLocCurrNum = 2));											// Pulse of the 2nd setpoint on schedule
xSchedSP3_PLS(CLK:= SetPoint.SW.xSched AND (SetPoint.wSPLocCurrNum = 3));											// Pulse of the 3rd setpoint on schedule
xSchedSP4_PLS(CLK:= SetPoint.SW.xSched AND (SetPoint.wSPLocCurrNum = 4));											// Pulse of the 4th setpoint on schedule

xRemotePLS(CLK:= SetPoint.SW.xRemote);																				// Remote mode transition pulse
xRemoteSetPLS(CLK:= SetPoint.SW.xRemote AND ConectCheck.Q AND (SetPoint.CW.xSPRemSet OR SetPoint.wSPRemSet.2));		// Remote setpoint change pulse
xRemoteNoConnPLS(CLK:= SetPoint.SW.xRemote AND NOT ConectCheck.Q);													// Pulse of SCADA communication loss


IF xSchedPLS.Q OR xSchedSP1_PLS.Q OR xSchedSP2_PLS.Q OR xSchedSP3_PLS.Q OR xSchedSP4_PLS.Q OR xRemoteNoConnPLS.Q THEN
	Cycle.rSP:= SetPoint.arrSPLocCurr[SetPoint.wSPLocCurrNum].rSP;													// Setting the setpoint by schedule
ELSIF xRemotePLS.Q OR xRemoteSetPLS.Q THEN
	Cycle.rSP:= SetPoint.rSPRem;																					// Setpoint setting remotely
END_IF

IF SetPoint.wSPRemSet.2 THEN
	SetPoint.wSPRemSet.2:= FALSE;
END_IF

IF SetPoint.CW.xSPRemSet THEN
	SetPoint.CW.xSPRemSet:= FALSE;
END_IF
